CREATE TABLE IF NOT EXISTS public.tbl_user_setting ( id bigint UNIQUE NOT NULL, notification boolean DEFAULT true, privacy_level character varying DEFAULT 'PUBLIC', theme character varying DEFAULT 'LIGHT', language character varying DEFAULT 'ENG', created_at timestamp without time zone, created_by character varying COLLATE pg_catalog."default", updated_at timestamp without time zone, updated_by character varying COLLATE pg_catalog."default", status character varying COLLATE pg_catalog."default" NOT NULL, CONSTRAINT tbl_user_setting_pkey PRIMARY KEY (id) )
TABLESPACE pg_default;
ALTER TABLE IF EXISTS public.tbl_user_setting OWNER to postgres;
